<?php/** * The main template file * * This is the most generic template file in a WordPress theme * and one of the two required files for a theme (the other being style.css). * It is used to display a page when nothing more specific matches a query. * E.g., it puts together the home page when no home.php file exists. * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/ * * @package sportapils */get_header();$category = get_the_category();?><main id="primary" class="site-main">    <div class="posts relative">        <?php set_query_var( 'category', $category[0] );?>        <?php if ( have_posts() ) : while ( have_posts() ) : the_post(); $count++ ?>            <?php if( $count == 1 && !is_paged() ) : ?>                <?php get_template_part( '/template-parts/archive/initial' ); ?>            <?php elseif($count == 2 && !is_paged()) : ?>                <div class="hexagon-list hexagon-category relative centered test">                    <?php get_template_part( '/template-parts/archive/loop' ); ?>            <?php else : ?>                <?php get_template_part( '/template-parts/archive/loop' ); ?>            <?php endif; ?>        <?php endwhile; ?>                <div class="clearfix"></div>                </div>                <div class="clearfix"></div>        <?php else : ?>            <!-- Posts not found Start -->            <div class="space-page-content-wrap relative">                <div class="space-page-content page-template box-100 relative">                    <h2><?php esc_html_e( 'Posts not found', 'sportapils' ); ?></h2>                    <p>                        <?php esc_html_e( 'No posts has been found. Please return to the homepage.', 'sportapils' ); ?>                    </p>                </div>            </div>            <!-- Posts not found End -->        <?php endif; ?>    </div></main><!-- #main --><?phpget_footer();?><div>    <div</div>