<?php/** * The main template file * * This is the most generic template file in a WordPress theme * and one of the two required files for a theme (the other being style.css). * It is used to display a page when nothing more specific matches a query. * E.g., it puts together the home page when no home.php file exists. * * @link https://developer.wordpress.org/themes/basics/template-hierarchy/ * * @package sportapils */get_header();$wp_query->set('posts_per_page', 11);$wp_query->query($wp_query->query_vars);$category = get_the_category();$category = get_queried_object();$light = false;if ($category->name == 'Light') $light = true;$count = 0;?><main id="primary" class="site-main">    <div class="posts relative <?php if($light) echo "light" ?>">        <?php if ( have_posts() ) : while ( have_posts() ) : the_post(); $count++ ?>            <?php set_query_var( 'count', $count );?>            <?php if( $count == 1) : ?>                <?php get_template_part( '/template-parts/archive/initial' ); ?>            <?php elseif($count == 2):  ?>                <div></div>                <div class="hexagon-list hexagon-category relative centered <?php if($light) echo "light" ?>">                    <?php get_template_part( '/template-parts/archive/loop' ); ?>            <?php else : ?>                <?php get_template_part( '/template-parts/archive/loop' ); ?>            <?php endif; ?>        <?php endwhile; ?>                </div>                <div class="clearfix"></div>                <div class="post-navigation">                    <a href="#" class="more-button button"><?php _e( 'More Posts', 'sportapils' ); ?></a>                    <div class="nav-links">                        <?php if (function_exists("pagination")) { pagination($wp_query->max_num_pages); } ?>                        <?php next_posts_link( 'Newer posts' ); ?>                    </div><!--nav-links-->                </div>        <?php else : ?>            <!-- Posts not found Start -->            <div class="space-page-content-wrap centered relative">                <div class="space-page-content page-template box-100 relative">                    <h2><?php esc_html_e( 'Posts not found', 'sportapils' ); ?></h2>                    <p>                        <?php esc_html_e( 'No posts has been found. Please return to the homepage.', 'sportapils' ); ?>                    </p>                </div>            </div>            <!-- Posts not found End -->        <?php endif; ?>    </div></main><!-- #main --><?phpget_footer();