<?php if ( !function_exists( 'infinite_scroll' ) ) {function infinite_scroll() {?>    <script type="text/javascript">        jQuery(document).ready(function($) {            $('.hexagon-list').infinitescroll({                navSelector: ".nav-links",                nextSelector: ".nav-links a:first",                itemSelector: ".hexagon",                loading: {                    msgText: "<?php esc_html_e( 'Loading more posts...', 'sportapils' ); ?>",                    finishedMsg: "<?php esc_html_e( 'Sorry, no more posts', 'sportapils' ); ?>"                },                errorCallback: function(){ $(".more-button").css("display", "none") }            });            $(window).unbind('.infscr');            $(".more-button").click(function(){                console.log($('.nav-links a').length);                $('.hexagon-list').infinitescroll('retrieve');                console.log($('.nav-links a').length);                return false;            });            $(window).load(function(){                if ($('.nav-links a').length) {                    $('.more-button').css('display','inline-block');                } else {                    $('.more-button').css('display','none');                }            });        });    </script><?php }}add_action( 'wp_footer', 'infinite_scroll' );