<?php$widget_3_big = wp_get_attachment_image_src(get_post_thumbnail_id(), 'full');$widget_3_big_mobile = wp_get_attachment_image_src(get_post_thumbnail_id(), 'full');$category = get_queried_object();if ($widget_3_big) { ?>    <div class="featured relative" onclick="location.href='<?php the_permalink(); ?>';">        <div class="main-title centered">            <div class="colored-icon"></div>            <h1 class="relative"><?php esc_html_e('All Posts' , 'sportapils' );?></h1>        </div>        <div class="overlay"></div>        <div class="img-wrapper">            <img src="<?php echo esc_url($widget_3_big[0]); ?>" alt="<?php the_title_attribute(); ?>">        </div>        <div class="content centered">            <div class="info">                <span class="date"><?php echo get_the_date(); ?></span>                <span class="author">@<?php echo get_the_author_meta('display_name');?></span>            </div>            <div class="title-wrapper">                <span class="title"><?php echo get_the_title() ?  esc_html(wp_trim_words( the_title('', '', false), 15, ' ...' )) : the_ID(); ?></span>            </div>            <div class="button-wrapper">                <a class="button colored" href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>">                    <?php esc_html_e('read post' , 'sportapils' );?>                </a>            </div>        </div>    </div><?php } ?>