<?php $hexagon_image = wp_get_attachment_image_src(get_post_meta( get_the_ID(), 'hexagon_featured_image', true), 'full');if(!$hexagon_image) $hexagon_image = wp_get_attachment_image_src(get_post_thumbnail_id(), 'full');$count = get_query_var('count') - 1;//Remove initial post from countif ($hexagon_image) { ?>    <a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>"       class="hexagon left <?php if (in_array($count % 10, [2,7])) echo 'grey ';       if (in_array($count % 10, [1,2,4,7,8,9])) echo 'colorless ';       if (in_array($count % 10, [1,8])) echo 'borderless '; ?>"       style="background-image: url(<?php echo esc_url($hexagon_image[0]); ?>);">        <?php if (!in_array($count % 10, [2,7])) {?><div class="overlay"></div><?php } ?>        <div class="content">            <div class="info">                <span class="date"><?php echo get_the_date(); ?></span>                <span class="author">@<?php echo get_the_author_meta('display_name');?></span>            </div>            <div class="title-wrapper">                <span class="title"><?php echo get_the_title() ?  esc_html(wp_trim_words( the_title('', '', false), 7, ' ...' )) : the_ID(); ?></span>            </div>            <div class="description-wrapper">                <span class="description">                    <?php echo esc_html(wp_trim_words( get_the_excerpt(), 30, ' ...' )); ?>                </span>            </div>        </div>        <div class="hexTop">            <?php if (!in_array($count % 10, [2,7])) { ?><div class="overlay"></div><?php } ?>        </div>        <div class="hexBottom">            <?php if (!in_array($count % 10, [2,7])) { ?><div class="overlay"></div><?php } ?>        </div>        <?php if (in_array($count % 10, [2,7])) { ?>            <div class="hexagon-overlay">                <div class="hexTop">                </div>                <div class="hexBottom">                </div>            </div>        <?php } ?>    </a><?php }