<?phpclass widget_ad extends WP_Widget {    /*  Widget #2 Setup  */    public function __construct() {        parent::__construct(false, $name = esc_html__('Sportapils ad block', 'sportapils' ), array(            'description' => esc_html__('Widget for displaying ads', 'sportapils' )        ));    }    /**     * How to display the widget on the screen.     */    function widget( $args, $instance ) {        extract( $args );        /* Our variables from the widget settings. */        $code = $instance['code'];        ?>        <div class="widget-ad centered relative">            <?php echo html_entity_decode($code); ?>        </div><!--widget-ad-->        <?php    }    /**     * Update the widget settings.     */    function update( $new_instance, $old_instance ) {        $instance = $old_instance;        /* Strip tags for title and name to remove HTML (important for text inputs). */        $instance['code'] = $new_instance['code'];        return $instance;    }    function form( $instance ) {        /* Set up some default widget settings. */        $defaults = array( 'code' => 'Enter ad code here');        $instance = wp_parse_args( (array) $instance, $defaults ); ?>        <!-- Ad code -->        <p>            <label for="<?php echo $this->get_field_id( 'code' ); ?>">Ad code:</label>            <textarea id="<?php echo $this->get_field_id( 'code' ); ?>" name="<?php echo $this->get_field_name( 'code' ); ?>" style="width:96%;" rows="6"><?php echo $instance['code']; ?></textarea>        </p>        <?php    }}add_action( 'widgets_init', 'widget_ad_register' );function widget_ad_register() {    register_widget( 'widget_ad' );}